    private SpdyCodecUtil() {
    }

    static int getUnsignedShort(ByteBuf buf, int offset) {
        return ((buf.getByte(offset) & 255) << 8) | (buf.getByte(offset + 1) & 255);
    }

    static int getUnsignedMedium(ByteBuf buf, int offset) {
        return (((buf.getByte(offset) & 255) << 16) | ((buf.getByte(offset + 1) & 255) << 8)) | (buf.getByte(offset + 2) & 255);
    }

    static int getUnsignedInt(ByteBuf buf, int offset) {
        return ((((buf.getByte(offset) & 127) << 24) | ((buf.getByte(offset + 1) & 255) << 16)) | ((buf.getByte(offset + 2) & 255) << 8)) | (buf.getByte(offset + 3) & 255);
    }

    static int getSignedInt(ByteBuf buf, int offset) {
        return ((((buf.getByte(offset) & 255) << 24) | ((buf.getByte(offset + 1) & 255) << 16)) | ((buf.getByte(offset + 2) & 255) << 8)) | (buf.getByte(offset + 3) & 255);
    }

    static boolean isServerId(int id) {
        return id % 2 == 0;
    }

    static void validateHeaderName(CharSequence name) {
        if (name == null) {
            throw new NullPointerException("name");
        } else if (name.length() == 0) {
            throw new IllegalArgumentException("name cannot be length zero");
        } else if (name.length() > 65535) {
            throw new IllegalArgumentException("name exceeds allowable length: " + name);
        } else {
            int i = 0;
            while (i < name.length()) {
                char c = name.charAt(i);
                if (c == '\u0000') {
                    throw new IllegalArgumentException("name contains null character: " + name);
                } else if (c >= 'A' && c <= 'Z') {
                    throw new IllegalArgumentException("name must be all lower case.");
                } else if (c > '') {
                    throw new IllegalArgumentException("name contains non-ascii character: " + name);
                } else {
                    i++;
                }
            }
        }
    }

    static void validateHeaderValue(CharSequence value) {
        if (value == null) {
            throw new NullPointerException("value");
        }
        for (int i = 0; i < value.length(); i++) {
            if (value.charAt(i) == '\u0000') {
                throw new IllegalArgumentException("value contains null character: " + value);
            }
        }
    }
